import React from 'react'

import './People.css'

import {useQuery, gql} from '@apollo/client';


const People = (props) => {

const DATA = gql 
`
  {
    person ($id:ID!) {
        name
        homeworld{
          name
        }
        species{
          name
        }

    }
  }

`;


    const id = "cGVvcGxlOjY="
    const {loading, error, data} = useQuery(DATA,{variables:{id },});
    if(loading) return <p>Loading..</p>
    if(error) return <p>Something wrong</p>

    const race = (props.data.species) ? props.data.species.name : 'Human';

    return(
    <div className='people'>
        <p className='people__name'>{props.data.name}</p>
        <p className='people__homeworld'>{race} from {props.data.homeworld.name}</p>
    </div>)

}

export default People;
