import React from 'react'

import './People.css'

import {useQuery, gql} from '@apollo/client';




const NOMBRES = gql 
`
  query Nombre($ide : ID)
  {
    person (id:$ide) {
        name
        homeworld{
          name
        }
        species{
          name
        }

    }
  }

`;

/*
    const id = "cGVvcGxlOjY="
    const {loading, error, data} = useQuery(DATA,{variables:{id },});
    if(loading) return <p>Loading..</p>
    if(error) return <p>Something wrong</p>
*/
const People = ({key,data,setSelectedId}) => {

  const onButton=(id)=>{
    setSelectedId(id);
  }

  const race = (data.species) ? data.species.name : 'Human';

    return(
    <div className='people'>
      <div>
      <p className='people__name'>{data.name}</p>
        <p className='people__homeworld'>{race} from {data.homeworld.name}</p>
      </div>
      <div>
        <button onClick={()=>onButton(data.id)}>ese</button>
      </div>


    </div>)

}

export default People;
