import React from 'react'

import './People.css'

import {useQuery, gql} from '@apollo/client';




const NOMBRES = gql 
`
  query Nombre($ide : ID)
  {
    person (id:$ide) {
        name
        homeworld{
          name
        }
        species{
          name
        }

    }
  }

`;

/*
    const id = "cGVvcGxlOjY="
    const {loading, error, data} = useQuery(DATA,{variables:{id },});
    if(loading) return <p>Loading..</p>
    if(error) return <p>Something wrong</p>
*/
const People = ({key,setSelectedId}) => {

  const ide = key;
  const {loading, error, data} = useQuery(NOMBRES,{variables:{ide}});

  if(loading) return (<LoadingCell/>)
    if(error) return (<NoticeCell/>)

  const onButton=(id)=>{
    setSelectedId(id);
  }

  const race = (data.person.species) ? data.person.species.name : 'Human';

    return(
    <div className='people'>
      <div>
        <p className='people__name'>{data.person.name}</p>
        <p className='people__homeworld'>{race} from {data.person.homeworld.name}</p>
      </div>

      <div>
        <button onClick={()=>onButton(key)}>ese</button>
      </div>


    </div>)

}

export default People;
